psql -h localhost -p 5432 -U admin -d chembl_36 -c "\copy (SELECT act.activity_id, act.assay_id, a.assay_type, act.standard_type, CAST(act.standard_value AS DOUBLE PRECISION) as standard_value, act.standard_units, act.standard_relation, act.pchembl_value, act.activity_comment, act.data_validity_comment, act.potential_duplicate, act.bao_endpoint, act.src_id, a.confidence_score, md.chembl_id, cs.canonical_smiles, csq.organism, td.pref_name as target_name FROM activities act JOIN assays a ON act.assay_id = a.assay_id JOIN target_dictionary td ON a.tid = td.tid JOIN target_components tc ON td.tid = tc.tid JOIN component_sequences csq ON tc.component_id = csq.component_id JOIN molecule_dictionary md ON act.molregno = md.molregno JOIN compound_structures cs ON act.molregno = cs.molregno) TO 'chembl_full_dump.csv' WITH CSV HEADER"