psql -h localhost -p 5432 -U admin -d chembl_36 -c "\copy (SELECT act.activity_id, act.assay_id, md.chembl_id, cs.standard_inchi_key, cs.canonical_smiles, act.standard_type, CAST(act.standard_value AS DOUBLE PRECISION) as standard_value, act.standard_units, act.standard_relation, act.pchembl_value, act.data_validity_comment, act.activity_comment, act.potential_duplicate, act.bao_endpoint, cp.mw_freebase, cp.alogp, cp.hba, cp.hbd, cp.psa, cp.rtb, cp.num_ro5_violations, md.max_phase, td.pref_name as target_name, csq.organism, a.assay_type, a.bao_format, a.confidence_score FROM activities act JOIN assays a ON act.assay_id = a.assay_id JOIN target_dictionary td ON a.tid = td.tid LEFT JOIN target_components tc ON td.tid = tc.tid LEFT JOIN component_sequences csq ON tc.component_id = csq.component_id JOIN molecule_dictionary md ON act.molregno = md.molregno JOIN compound_structures cs ON act.molregno = cs.molregno LEFT JOIN compound_properties cp ON md.molregno = cp.molregno) TO 'chembl_enhanced_dump200.csv' WITH CSV HEADER"